create table board
(
    board_no int generated by default as identity,
    register varchar2(255) not null,
    password varchar2(255) not null,
    board_title varchar2(255) not null,
    contents varchar2(500) default '-',
    reg_cnt int default 0,
    reg_date Timestamp default now(),
    modi_date Timestamp default null,
    del_YN char(1) default 'N',
    del_date Timestamp default null,
    primary key (board_no)
);

